--
-- Table structure for table `cms_projects`
--

CREATE TABLE `cms_projects` (
  `id` int(11) NOT NULL,
  `category_id` int(11) DEFAULT NULL,
  `title` varchar(255) NOT NULL,
  `slug` varchar(255) NOT NULL,
  `short_description` varchar(500) DEFAULT NULL,
  `location` varchar(255) DEFAULT NULL COMMENT 'Project location (City, Country)',
  `featured_image` varchar(500) DEFAULT NULL,
  `content` longtext DEFAULT NULL,
  `gallery_images` text DEFAULT NULL COMMENT 'JSON array of image paths',
  `view_order` int(11) NOT NULL DEFAULT 0,
  `is_featured` tinyint(1) NOT NULL DEFAULT 0,
  `is_active` tinyint(1) NOT NULL DEFAULT 1,
  `seo_title` varchar(255) DEFAULT NULL,
  `meta_description` varchar(500) DEFAULT NULL,
  `meta_robots` varchar(50) DEFAULT 'index,follow',
  `canonical_url` varchar(500) DEFAULT NULL,
  `og_title` varchar(255) DEFAULT NULL,
  `og_description` varchar(500) DEFAULT NULL,
  `og_image` varchar(500) DEFAULT NULL,
  `og_type` varchar(50) DEFAULT 'website',
  `twitter_card` varchar(50) DEFAULT 'summary_large_image',
  `twitter_title` varchar(255) DEFAULT NULL,
  `twitter_description` varchar(500) DEFAULT NULL,
  `twitter_image` varchar(500) DEFAULT NULL,
  `schema_type` varchar(100) DEFAULT 'Article',
  `schema_data` text DEFAULT NULL COMMENT 'JSON-LD structured data',
  `hreflang_tags` text DEFAULT NULL COMMENT 'JSON array of hreflang entries',
  `redirect_to` varchar(500) DEFAULT NULL,
  `redirect_type` int(3) DEFAULT NULL COMMENT '301, 302, or 410',
  `published_at` timestamp NULL DEFAULT NULL,
  `unpublished_at` timestamp NULL DEFAULT NULL,
  `noindex_on_staging` tinyint(1) NOT NULL DEFAULT 1,
  `sitemap_priority` decimal(2,1) DEFAULT 0.5,
  `sitemap_changefreq` varchar(20) DEFAULT 'monthly',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

