INSERT INTO `cms_projects` (`id`, `category_id`, `title`, `slug`, `short_description`, `location`, `featured_image`, `content`, `gallery_images`, `view_order`, `is_featured`, `is_active`, `seo_title`, `meta_description`, `meta_robots`, `canonical_url`, `og_title`, `og_description`, `og_image`, `og_type`, `twitter_card`, `twitter_title`, `twitter_description`, `twitter_image`, `schema_type`, `schema_data`, `hreflang_tags`, `redirect_to`, `redirect_type`, `published_at`, `unpublished_at`, `noindex_on_staging`, `sitemap_priority`, `sitemap_changefreq`, `created_at`, `updated_at`) VALUES
(1, 7, 'Sindalah Luxury Island', 'hospitality', 'Located in the Red Sea off the Saudi coast, the artificial island spans 840,000 sqm. It is 2 km long, with a width ranging from 200m to 1 km. Sindalah Island features a sports club, spa, wellness centre, 51 luxury retail outlets, a golf course, and 38 restaurants.', 'NEOM, KSA', 'uploads/images/14.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><span style=\"color: rgb(22, 145, 121);\"><strong><span class=\"cf0\">SINDALAH, LUXURY ISLAND</span></strong></span></h3>\r\n<p class=\"pf0\"><strong><span style=\"color: rgb(52, 73, 94);\"><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Hospitality_7.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Luxury Tourism</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering&nbsp;<!--StartFragment --></span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span class=\"cf0\"><!--EndFragment --></span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\">&nbsp;\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Saudi Arabia\'s Neom has officially opened </span><span class=\"cf0\">Sindalah</span><span class=\"cf0\"> Island, its first publicly accessible region. Located in the Red Sea off the Saudi coast, the artificial island spans 840,000 sqm. It is 2 km long, with a width ranging from 200m to 1 km. </span><span class=\"cf0\">Sindalah</span><span class=\"cf0\"> Island features a sports club, spa, wellness </span><span class=\"cf0\">centre</span><span class=\"cf0\">, 51 luxury retail outlets, a golf course, and 38 restaurants. Situated 5km from the mainland, it is accessible by boat in 15 minutes. The island&rsquo;s elevation ranges from 4m to 23m, and it is 17 hours away from most Mediterranean yachting destinations. The project consists of six clusters, offering a mix of residential, commercial, leisure, and hospitality spaces.</span>&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the role involved conducting design reviews at all stages, from pre-concept to design delivery. This included overseeing the contractor&rsquo;s designs, ensuring compliance with regional regulations and standards, and addressing potential issues. The work covered fire fighting systems (hydrants, hose reels, sprinklers, fire extinguishers), standpipe systems for emergency water access, water spray systems for high-risk areas, and clean agent systems for sensitive environments (e.g., electrical rooms). Additionally, the fire alarm system&rsquo;s design ensured early detection, while emergency lighting was planned for safe evacuation during power failures. The AHJ&rsquo;s responsibility included approving designs, monitoring installation, and ensuring adherence to safety standards for people and property protection.</span></p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/36.jpg,uploads/images/37.jpg', 7, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:08:55', '2025-12-15 13:27:44'),
(2, 4, 'Advanced Health Care, Oxagon', 'healthcare', 'With a built-up area of 23,115 sqm and a capacity of 30 beds, the facility is designed to stabilize critically ill patients and provide specialized care. The hospital spans five floors, including a rooftop helipad, and features a range of services such as surgical departments, imaging, emergency care, outpatient facilities, and a retail pharmacy. \r\n', 'NEOM, KSA', 'uploads/images/Healthcare_1_-_Copy.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h4 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">ADVANCE HEALTH CENTRE, OXAGON</span></strong></h4>\r\n<p class=\"pf0\"><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Healthcare_1.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client:</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector:</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Healthcare</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services:</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span class=\"cf0\">The OXAGON Advanced Health Centre in NEOM, Saudi Arabia, will offer comprehensive healthcare services, including 24/7 primary care, advanced diagnostics, labor and delivery, and urgent care. With a built-up area of 23,115 sqm and a capacity of 30 beds, the facility is designed to stabilize critically ill patients and provide specialized care. The hospital spans five floors, including a rooftop helipad, and features a range of services such as surgical departments, imaging, emergency care, outpatient facilities, and a retail pharmacy. Located on a 9,225 sqm plot, the center also includes essential support areas such as a kitchen, clinical laboratory, and waste management facilities.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Served as the Authority Having Jurisdiction (AHJ) and conducts design reviews from concept to detailed design stage of the asset.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/12.jpg,uploads/images/Healthcare_5.jpg,uploads/images/Healthcare_4.jpg,uploads/images/Healthcare_3.png,uploads/images/Healthcare_1.png', 5, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:14:09', '2025-12-15 13:25:10'),
(3, 5, 'NEOM Construction Village', 'residential', 'The NEOM Construction Village project will include accommodation, office spaces, and essential support facilities for construction workers and staff. Designed to serve as a self-contained hub, the village will provide the necessary infrastructure to support large-scale construction activities within the NEOM region. \r\n', 'NEOM, KSA', 'uploads/images/Res-11.jpg', '<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3 class=\"pf0\"><strong><span style=\"color: rgb(22, 145, 121);\"><span class=\"cf0\">NEOM CONSTRUCTION VILLAGE</span></span></strong></h3>\r\n<p class=\"pf0\"><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA</span></strong></span></p>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Res-11.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Residential</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The NEOM Construction Village project will include accommodation, office spaces, and essential support facilities for construction workers and staff. Designed to serve as a self-contained hub, the village will provide the necessary infrastructure to support large-scale construction activities within the NEOM region. This stand-alone facility will ensure a functional and efficient environment for the workforce involved in the development of NEOM.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work</span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Serving as the AHJ, the scope of services includes reviewing design drawings for compliance with NFPA, SBC, and related fire safety codes. Key tasks involve defining occupancy classifications, verifying fire resistance ratings, and assessing means of egress for all buildings. Fire protection systems, including alarms, sprinklers, and fire-rated barriers, will be specified as required. Deliverables include detailed fire strategy documents covering zoning, egress plans, and compartmentation.</span></p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/Res-8.png,uploads/images/Res-7.jpg,uploads/images/Res-9.png,uploads/images/Res-4.png,uploads/images/Res-6.png', 6, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:22:41', '2025-12-15 13:25:21'),
(4, 8, 'MBZ Dubai & Fujairah Station', 'mbz-dubai-fujairah-station', 'The Etihad Rail Network comprises a proposed railway network, which will link freight facilities and passenger stations, and form a part of the planned Gulf Cooperation Council (GCC) railway network.', 'Dubai, UAE', 'uploads/images/Mass_Transit_5_jpg.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">FIRE RISK ASSESSMENT OF MBZ, DUBAI &amp; FUJAIRAH STATIONS</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(0, 0, 0);\">DUBAI, UAE</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mass_Transit_5_jpg.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">EFRC </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Mass Transit, Transport- Rail </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Review and update of the Fire Risk Assessment report</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">This project involves the review and update of the Fire Risk Assessment Reports for the Etihad Railway Stations located at MBZ, Dubai, and Fujairah. The Etihad Rail Network comprises a proposed railway network, which will link freight facilities and passenger stations, and form a part of the planned Gulf Cooperation Council (GCC) railway network. It will run from the Saudi Arabian border at </span><span class=\"cf0\">Ghweifat</span><span class=\"cf0\"> in the west, via or close to the cities of the Gulf coast, to the Northern Emirates and the East coast. There will also be a line running from Abu Dhabi to Al Ain, with intended connection into the Sultanate of Oman, as well as an existing branch line to the gas facilities at Shah and </span><span class=\"cf0\">Habshan</span><span class=\"cf0\">.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The objective is to ensure that the updated reports align with the latest requirements of NFPA 130, NFPA 551, and the UAE Fire and Life Safety Code of Practice. Our scope includes evaluating the existing assessments, identifying probable causes of fire and life safety risks, updating risk ratings, and proposing effective control and mitigation measures to enhance the overall safety and compliance of the stations.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/40.jpg,uploads/images/Mass_Transit_4.jpg,uploads/images/Mass_Transit_2.jpg,uploads/images/Mass_Transit_3.jpg', 8, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:31:03', '2025-12-15 13:25:46'),
(5, 10, 'Dubai Commercity Office Building', 'dubai-commercity-office-building-uae', 'Dubai Commer City is a large-scale commercial development comprising multiple office buildings arranged in dedicated clusters. Located in Umm Ramool, the project forms part of Dubai\'s strategic initiative to enhance its e-commerce and commercial infrastructure. ', 'Dubai, UAE', 'uploads/images/Commercial_17.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h4 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">DUBAI COMMERCITY OFFICE BUILDING </span></strong></h4>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">DUBAI, UAE</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Commercial_17.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">P&amp;T Group, Dubai</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Commercial</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design &amp; Strategy</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Peer review of FLS systems</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Dubai&nbsp;</span><span class=\"cf0\">Commerc</span><span class=\"cf0\">ity is a large-scale commercial development comprising multiple office buildings arranged in dedicated clusters. Located in Umm </span><span class=\"cf0\">Ramool</span><span class=\"cf0\">, the project forms part of Dubai\'s strategic initiative to enhance its e-commerce and commercial infrastructure. The development includes a mix of high rise office buildings, shared amenities, and supporting infrastructure designed to serve various business tenants. The clustered layout, interconnected spaces, and shared access zones presented unique planning and operational considerations, particularly from a fire and life safety perspective.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Appointed as the Fire and Life Safety (FLS) Consultant for the project, responsible for providing complete design review and strategy development services from the concept stage through to tender stage delivery. A comprehensive fire and life safety strategy was formulated in accordance with the UAE Fire and Life Safety Code and relevant NFPA standards. Architectural layouts for each cluster were reviewed for code compliance, with recommendations issued for required modifications to ensure safe means of egress, proper compartmentation, and firefighting access. Fire and life safety layouts and reports were prepared for authority submissions and coordination with design stakeholders. All major FLS systems, including fire protection, fire alarm, smoke management, and mechanical ventilation, were evaluated and verified for compliance. Technical support was provided throughout the authority approval process to ensure full compliance and project alignment with regulatory requirements.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/30.jpg,uploads/images/Commercial_13.png,uploads/images/Commercial_10.png,uploads/images/Commercial_7.png,uploads/images/Commercial_9_png.jpg', 10, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:36:12', '2025-12-15 11:47:39'),
(6, 9, 'Marhaba Lounge, Dubai International Airport', 'marhaba-lounge-dubai-international-airport', 'Appointed by Design Motives to provide fire and life safety consulting for the Marhaba Lounge at Concourse C, Dubai International Airport (DXB). \r\n', 'Dubai, UAE', 'uploads/images/Fitout_5.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h4 class=\"pf0\"><strong><span style=\"color: rgb(22, 145, 121);\"><span class=\"cf0\">MARHABA </span><span class=\"cf0\">LOUNGE</span></span></strong></h4>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">DUBAI INTERNATIONAL AIRPORT-CONCOURSE-C</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Fitout_5.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Design Motives</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Hospitality, Airport Services</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Architectural Code Consulting</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Design Approval</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Inspections &amp; Compliance Checks</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">System Testing &amp; Commissioning</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Handover Inspections</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span class=\"cf0\">Appointed by Design Motives to provide fire and life safety consulting for the Marhaba Lounge at Concourse C, Dubai International Airport (DXB). The scope of work included architectural code consulting, design approval, and ensuring compliance with fire safety regulations. Conducted inspections to verify compliance, working alongside the DA </span><span class=\"cf0\">Fitout</span><span class=\"cf0\"> and DA AFS teams during these checks.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Provided architectural code consulting and design approval to ensure compliance with fire safety standards. Conducted inspections to verify that the construction complied with the approved AFS drawings. Performed testing and commissioning of fire and life safety systems to ensure functionality and compliance. Worked closely with the DA </span><span class=\"cf0\">Fitout</span><span class=\"cf0\"> and DA AFS teams during handover inspections to finalize the project. The involvement ensured that the Marhaba Lounge at Concourse C met all fire and life safety requirements, offering a secure and compliant space for passengers at DXB.</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/Fitout_11.png,uploads/images/Fitout_12.jpg,uploads/images/Fitout_10.png,uploads/images/Fitout_18.jpg,uploads/images/Fitout_3.png', 9, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 0, 0.5, 'monthly', '2025-11-20 10:49:29', '2025-12-15 13:26:01'),
(7, 11, 'North Interim Logistic Jetty', 'north-interim-logistic-jetty', 'A vital facility designed to support and meet the operational and maintenance needs of assets on Ummahat and Shebara Islands, located 700 meters from the mainland. The jetty includes portacabin buildings, floating pontoons, a cargo quay, shaded structures, a fuel dispensing facility, utility areas, and parking spaces.', 'Red Sea Global, KSA', 'uploads/images/Marina_1_1.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">NORTH INTERIM LOGISTIC JETTY</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">RED SEA GLOBAL, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<figure class=\"image\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Marina_1_1.jpg\" alt=\"\" width=\"100%\">\r\n<figcaption></figcaption>\r\n</figure>\r\n</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">RED SEA GLOBAL, KSA </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Marina</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">FLS Strategy</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire Risk Assessment</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Appointed as the Fire and Life Safety (FLS) Design Consultant for the North Interim Logistics Jetty, a vital facility designed to support and meet the operational and maintenance needs of assets on </span><span class=\"cf0\">Ummahat</span><span class=\"cf0\"> and </span><span class=\"cf0\">Shebara</span><span class=\"cf0\"> Islands, located 700 meters from the mainland. The jetty includes portacabin buildings, floating pontoons, a cargo quay, shaded structures, a fuel dispensing facility, utility areas, and parking spaces.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Developed the Fire and Life Safety (FLS) strategy and design for the entire facility.</span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Designed fire detection and suppression systems tailored to the specific needs of the facility. Addressed fire safety challenges related to fuel dispensing and cargo operations, ensuring full integration of fire safety systems into the complex logistics setup. The involvement ensured the North Interim Logistics Jetty met all fire safety requirements, supporting the safe and efficient operation of the facility in compliance with local and international safety standards.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 11, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 10:56:35', '2025-12-15 12:56:30'),
(8, 3, 'Utility Tunnels, Oxagon', 'roads-tunnels', 'The Utility Tunnels in Oxagon, NEOM, consist of wet and dry utility tunnels. The wet tunnels carry pressurized infrastructure piping with minimal hazards, while the dry tunnels house medium-voltage cables and ICT cables. These dry tunnels present a high risk of cable fires due to overheating, short-circuiting, or deterioration', 'NEOM, KSA', 'uploads/images/Tunnel_3.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h4><span style=\"color: rgb(22, 145, 121);\"><strong><span class=\"cf0\">TUNNELS, OXAGON</span></strong></span></h4>\r\n<p><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Tunnel_3.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p><span class=\"cf0\">Dry &amp; Wet Utility Tunnels</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering</span></p>\r\n<p><span class=\"cf0\">Performance Based Design (CFD + Egress Modelling)</span></p>\r\n<p><span class=\"cf0\">Tunnel Ventilation &amp; Tunnel FLS Strategy</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">The Utility Tunnels in </span><span class=\"cf0\">Oxagon</span><span class=\"cf0\">, NEOM, consist of wet and dry utility tunnels. The wet tunnels carry pressurized infrastructure piping with minimal hazards, while the dry tunnels house medium-voltage cables and ICT cables. These dry tunnels present a high risk of cable fires due to overheating, short-circuiting, or deterioration. If temperatures exceed safe limits, it can cause cable performance issues and, in extreme cases, lead to catastrophic fires, damaging assets and compromising the tunnel&rsquo;s structural integrity. To prevent overheating, the tunnels are equipped with ventilation systems, including large axial fans for cooling. In case of fire, a fire safety system is in place to protect both life and property. Additionally, the wet tunnels are designed with ventilation to maintain air quality and allow for heat, smoke control, and purging if necessary.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team conducted design reviews from the concept phase through to the detailed design stage of the utility tunnels. This oversight ensured that the tunnels meet all safety, operational, and regulatory requirements, with particular emphasis on fire prevention, structural integrity, and effective ventilation systems.</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/Tunnel_4.jpg,uploads/images/Tunnel_2.png,uploads/images/Tunnel_1.png', 4, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 11:00:19', '2025-12-15 12:05:26'),
(9, 1, 'Velanna International Airport', 'velanna-international-airport', 'The VELANAA International Airport project involved evaluating the fire and life safety systems of six buildings, including the passenger terminal, sea plane terminal, hangars, power plant, and other supporting utilities.', 'Maldives', 'uploads/images/Aviation_2.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h4><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">VELANAA INTERNATIONAL AIRPORT</span></strong></h4>\r\n<p><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">MALDIVES</span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Aviation_2.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p><span class=\"cf0\">Maldives Airport Authority, Maldives</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p><span class=\"cf0\">Airport, Aviation, Infrastructure</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p><span class=\"cf0\">Retrospective Fire Safety Evaluation</span></p>\r\n<p><span class=\"cf0\">System Testing &amp; Inspection</span></p>\r\n<p><span class=\"cf0\">Compliance Assessment</span></p>\r\n<p><span class=\"cf0\">Cost-Effective Solutions</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">The VELANAA International Airport project involved evaluating the fire and life safety systems of six buildings, including the passenger terminal, sea plane terminal, hangars, power plant, and other supporting utilities.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Employed by the Maldives Airport Authority to carry out a retrospective fire and life safety evaluation of the airport&rsquo;s facilities. The team identified several compliance issues and provided cost-effective solutions to achieve the necessary fire and life safety standards.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Conducted a retrospective fire and life safety evaluation for the airport&rsquo;s six buildings. Identified compliance issues and provided recommendations for achieving minimum compliance with fire safety codes.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Conducted system testing and inspection of various fire protection systems across the facilities. Collaborated with the Maldives Airport Authority and a specialist team from Dubai for onsite inspections and assessments.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">The involvement ensured that </span><span class=\"cf0\">Velanaa</span><span class=\"cf0\"> International Airport met fire and life safety standards, addressing key compliance issues while providing cost-effective solutions to enhance the overall safety of the facilities.</span></p>\r\n<h3 style=\"text-align: justify;\"><!--EndFragment --></h3>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/10.jpg,uploads/images/Aviation10.png,uploads/images/Aviation_8.jpg,uploads/images/Aviation_6.png,uploads/images/Aviation_3.jpg', 2, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 11:16:40', '2025-12-15 12:04:12'),
(10, 13, 'Innovation Campus, Oxagon', 'innovation-campus-oxagon', 'The Oxagon Research & Innovation Campus is designed to be a hub for cutting-edge research and development, fostering innovation from applied research to early-stage commercialization. ', 'NEOM, KSA', 'uploads/images/Edu.jpg', '<div class=\"container py-4\">\r\n<div class=\"row g-4\">\r\n<div class=\"col-md-4\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">INNOVATION CAMPUS, OXAGON</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Edu.jpg\" alt=\"\" width=\"100%\"></div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Education/campus</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The </span><span class=\"cf0\">Oxagon</span><span class=\"cf0\"> Research &amp; Innovation Campus is designed to be a hub for cutting-edge research and development, fostering innovation from applied research to early-stage commercialization. The campus will feature a dynamic mix of facilities, including laboratories, workshops, office spaces, MEP and storage areas, and communal amenities such as retail outlets, F&amp;B units, conference facilities, and outdoor gathering spaces.</span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">This vibrant community aims to attract world-class researchers, industries, and entrepreneurs, blending workspaces optimized for collaboration, versatile micro-neighborhoods, hospitality offerings, and biophilic designs with abundant greenery and water features to enhance satisfaction, health, and well-being. The campus offers diverse workspace types, from labs and open-plan offices to column-free workshops, complemented by restaurants, sports and wellness facilities, and cultural event spaces, creating a seamless \"live, work, and play\" environment..</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Leading and conducting comprehensive Fire &amp; Life Safety design reviews across all project stages from master planning and concept development through schematic, detailed design, and construction documentation phases. Responsibilities included reviewing and validating fire strategies, evacuation provisions, active and passive fire protection systems, life safety compliance, and performance-based design submissions. Additionally, technical guidance, code interpretation, and design advisory services. provided to project teams and stakeholders throughout the design lifecycle, ensuring alignment with applicable codes, standards, and authority requirements.</span></p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/Education_2.png,uploads/images/Edu.jpg,uploads/images/Education_2.jpg', 1, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 11:22:07', '2025-12-15 13:24:15'),
(11, 2, 'Hyper Scale Data Centre, Oxagon', 'hyper-scale-data-centre-oxagon', 'The NEOM Data Centre is a state-of-the-art, two-story business building designed primarily for data processing. Spanning a catchment built-up area of 32,488 mÂ², the center is a critical upgrade to NEOM\'s ICT infrastructure.', 'NEOM, KSA', 'uploads/images/Mission_1.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">HYPER SCALE DATA CENTRE, OXAGON</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mission_1.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Mission Critical, Data Centre</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The NEOM Data Centre is a state-of-the-art, two-story business building designed primarily for data processing. Spanning a catchment built-up area of 32,488 m&sup2;, the center is a critical upgrade to NEOM\'s ICT infrastructure. Strategically located in&nbsp;</span><span class=\"cf0\">Oxagon</span><span class=\"cf0\">, the hub of advanced and clean industries in NEOM, the facility delivers exceptional and reliable network speeds. This advanced data center is vital for enabling secure information sharing, fast decision-making, and robust connectivity. It supports the demands of construction across the NEOM region, ensures uninterrupted communication for residents and businesses, and optimizes operational costs.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team conducted comprehensive design reviews spanning from the design delivery stage to Shop drawing stage, ensuring adherence to regulatory and operational standards.</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 3, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 11:26:10', '2025-12-15 12:05:06'),
(12, 12, 'Hydrogen Innovation Development Centre', 'hydrogen-innovation-development-centre', 'The Hydrogen Innovation Development Center (HIDC) in NEOM Industrial City aims to establish a cutting-edge hub for green hydrogen technology. Located 36 km north of Duba City, the center will feature advanced facilities for hydrogen production through alkaline water electrolysis, along with methanol synthesis and methanol-to-gasoline production units. ', 'NEOM, KSA', 'uploads/images/Industrial_1.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">HYDROGEN INNOVATION DEVELOPMENT CENTRE (HIDC)</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Industrial_1.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(0, 0, 0);\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Research, Industrial</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The Hydrogen Innovation Development Center (HIDC) in NEOM Industrial City aims to establish a cutting-edge hub for green hydrogen technology. Located 36 km north of&nbsp;</span><span class=\"cf0\">Duba</span><span class=\"cf0\"> City, the center will feature advanced facilities for hydrogen production through alkaline water electrolysis, along with methanol synthesis and methanol-to-gasoline production units. The project will be executed in phases, starting with the electrolysis unit and expanding to include methanol and gasoline production. The HIDC will produce green hydrogen for both mobility and fuel production, with the capacity for future expansion. This facility is a key part of NEOM&rsquo;s vision for sustainable energy innovation and will contribute to the region\'s green hydrogen and fuel goals.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(0, 0, 0);\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team provided design reviews from concept to detailed design, ensuring compliance with safety and regulatory standards. This included reviewing the plant design, FERA Study report, Assumption Register, QRA Study Report etc.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 12, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-20 11:28:15', '2025-12-15 12:42:40'),
(14, 13, 'Research & Innovation Campus, Oxagon', 'research-innovation-campus-oxagon', 'The Education, Research, and Innovation (ERI) Campus serves as a cornerstone of NEOM\'s vision to create an integrated ecosystem that unites Higher Education, the Education Research and Innovation Foundation (ERIF), and the Talent Academy (TA). ', 'NEOM, KSA', 'uploads/images/Education_2.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3 class=\"col-md-6\"><span style=\"color: rgb(22, 145, 121);\"><strong><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 115%; font-family: Calibri, sans-serif;\">EDUCATION RESEARCH AND INNOVATION CAMPUS (ERI), OXAGON</span></strong></span></h3>\r\n<h3><span style=\"color: rgb(52, 73, 94);\"><strong><span lang=\"EN-US\" style=\"font-size: 16pt; line-height: 115%; font-family: Calibri, sans-serif;\"><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 115%; font-family: Calibri, sans-serif;\">NEOM, KINGDOM OF SAUDI ARABIA</span></span></strong></span></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Education_2.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Education/campus</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The Education, Research, and Innovation (ERI) Campus serves as a cornerstone of NEOM&rsquo;s vision to create an integrated ecosystem that unites Higher Education, the Education Research and Innovation Foundation (ERIF), and the Talent Academy (TA). Designed to foster collaboration between academia, research, and industry, the campus provides shared learning, research, and social spaces that encourage seamless interaction among students, faculty, and professionals.&nbsp;</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work </span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Leading and conducting comprehensive Fire &amp; Life Safety design reviews across all project stages from master planning and concept development through schematic, detailed design, and construction documentation phases. Responsibilities included reviewing and validating fire strategies, evacuation provisions, active and passive fire protection systems, life safety compliance, and performance-based design submissions. Additionally, technical guidance, code interpretation, and design advisory services. provided to project teams and stakeholders throughout the design lifecycle, ensuring alignment with applicable codes, standards, and authority requirements.</span></p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/Education_5.png,uploads/images/Education_3_1.jpg,uploads/images/Education_1.jpg', 1, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-11-26 13:10:40', '2025-12-15 12:03:41');
INSERT INTO `cms_projects` (`id`, `category_id`, `title`, `slug`, `short_description`, `location`, `featured_image`, `content`, `gallery_images`, `view_order`, `is_featured`, `is_active`, `seo_title`, `meta_description`, `meta_robots`, `canonical_url`, `og_title`, `og_description`, `og_image`, `og_type`, `twitter_card`, `twitter_title`, `twitter_description`, `twitter_image`, `schema_type`, `schema_data`, `hreflang_tags`, `redirect_to`, `redirect_type`, `published_at`, `unpublished_at`, `noindex_on_staging`, `sitemap_priority`, `sitemap_changefreq`, `created_at`, `updated_at`) VALUES
(15, 3, 'Road 8784 Mountain Area-Road Tunnels', 'road-8784-mountain-area-road-tunnels', 'This Road Tunnel designed for separate traffic travel, located in the Kingdom of Saudi Arabia.', 'NEOM, KSA', 'uploads/images/Tunnel_2.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3><span style=\"color: rgb(22, 145, 121);\"><strong><span class=\"cf0\">ROAD 8784 MOUNTAIN AREA-ROAD TUNNELS</span></strong></span></h3>\r\n<p><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Tunnel_2.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p><strong><span class=\"cf0\">Client:</span></strong></p>\r\n<p><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Sector:</span></strong></p>\r\n<p><span class=\"cf0\">Roads, Transportation</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Key Services:</span></strong></p>\r\n<p><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">This Road Tunnel designed for separate traffic travel, located in the Kingdom of Saudi Arabia. Serving as the Authority Having Jurisdiction (AHJ), the scope of services includes reviewing design drawings for compliance with fire safety codes such as NFPA, SBC, and particularly NFPA 502. This involves defining building occupancy classifications, ensuring adherence to travel distance limits, and evaluating structural fire resistance. The AHJ will also specify required fire protection systems (e.g., alarms, sprinklers), ensure proper means of egress, and address interior finishes according to fire safety standards. Deliverables will include comprehensive fire strategy documents, including zoning layouts, exit signage, and fire compartmentation details.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team conducted design reviews from the concept phase through to the detailed design stage of the utility tunnels. This oversight ensured that the tunnels meet all safety, operational, and regulatory requirements, with particular emphasis on fire prevention, structural integrity, and effective ventilation systems.</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>', 'uploads/images/Tunnel_4.jpg,uploads/images/Tunnel_2.png,uploads/images/Tunnel_1.png', 4, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-01 05:04:06', '2025-12-15 13:24:52'),
(16, 1, 'Fuel Farm, Velanaa International Airport', 'fuel-farm-velanaa-international-airport', 'The Fuel Farm at VELANAA International Airport, Maldives, is a critical facility for storing and managing aviation fuel.', 'Maldives', 'uploads/images/Aviation_3.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3><span style=\"color: rgb(22, 145, 121);\"><strong><span class=\"cf0\">FUEL FARM, VELANAA INTERNATIONAL AIRPORT</span></strong></span></h3>\r\n<p><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">MALDIVES </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Aviation_3.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"container py-4\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p><span class=\"cf0\">Maldives Airport Authority, Maldives</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p><span class=\"cf0\">Airport, Fuel Storage</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p><span class=\"cf0\">Fire Safety Evaluation</span></p>\r\n<p><span class=\"cf0\">System Testing &amp; Inspection</span></p>\r\n<p><span class=\"cf0\">Cost-Effective Solutions</span></p>\r\n<p><span class=\"cf0\">Compliance Assessment</span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">The Fuel Farm at VELANAA International Airport, Maldives, is a critical facility for storing and managing aviation fuel. Appointed by the Maldives Airport Authority to conduct a fire and life safety evaluation of the facility and address specific concerns raised by the project consultant. Team performed a detailed evaluation and proposed cost-effective solutions to achieve minimum compliance with fire and life safety regulations.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Conducted a comprehensive fire safety evaluation of the fuel farm facility.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Identified specific concerns and provided cost-effective solutions for compliance.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">Performed system testing and inspections of various fire protection systems at the fuel farm. Collaborated with the Maldives Airport Authority and supported by a team of specialists from Dubai to carry out on-site testing and inspections.</span></p>\r\n<p style=\"text-align: justify;\"><span class=\"cf0\">The involvement ensured that the Fuel Farm met essential fire and life safety standards, addressing key compliance issues while providing practical, cost-efficient solutions</span></p>\r\n<h3 style=\"text-align: justify;\"><!--EndFragment --></h3>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/10.jpg,uploads/images/Aviation10.png,uploads/images/Aviation_8.jpg,uploads/images/Aviation_6.png,uploads/images/Aviation_3.jpg,uploads/images/Aviation_1.jpg,uploads/images/Aviation_5.jpg', 2, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-01 05:18:02', '2025-12-15 13:24:23'),
(17, 10, 'Woohoo Restaurant', 'woohoo-restaurant', 'Wohoo Restaurant, located on the ground floor of The Address Boulevard Hotel, is a completed premium dining destination designed to complement the sophistication of its setting. ', 'Dubai, UAE', 'uploads/images/Commercial_13.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">WOOHOO RESTAURANT</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">DUBAI, UAE</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Commercial_13.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Munwel</span><span class=\"cf0\"> Interiors, UAE</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Commercial, Business</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Key Services</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety (FLS) Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Smoke Management System Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire Protection &amp; Fire Alarm System Review</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Code Compliance Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Wohoo</span><span class=\"cf0\"> Restaurant, located on the ground floor of The Address Boulevard Hotel, is a completed premium dining destination designed to complement the sophistication of its setting. With a total floor area of 1,370 sqm, the project blends modern design, high-quality finishes, and functional planning to deliver an elegant experience for guests.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work </span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The scope of work included reviewing architectural drawings for full compliance with fire and life safety requirements and developing FLS strategy drawings in accordance with applicable codes and standards. Fire protection, fire alarm, and emergency lighting system drawings were reviewed to ensure compliance with design requirements and regulations, with full coordination between systems and architectural layouts. In addition, smoke management system designs were developed and reviewed in line with applicable codes and standards, and detailed drawings were prepared for approval and implementation.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 10, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 11:07:10', '2025-12-15 13:26:15'),
(18, 2, 'Telecommunication Centre, Oxagon', 'telecommunication-centre-oxagon', 'The NEOM Telecommunications Center in Oxagon is NEOM\'s first dedicated digital hub, providing the core infrastructure for high-speed connectivity, cloud services, and smart technologies. ', 'NEOM, KSA', 'uploads/images/Mission_2.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">TELECOMMUNICATION CENTRE, OXAGON</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mission_2.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Mission Critical, Data Centre</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The NEOM Telecommunications Center in&nbsp;</span><span class=\"cf0\">Oxagon</span><span class=\"cf0\"> is NEOM&rsquo;s first dedicated digital hub, providing the core infrastructure for high-speed connectivity, cloud services, and smart technologies. Built in phases and to Uptime Tier III standards, the center ensures resilient and scalable operations, supporting the growing needs of NEOM&rsquo;s industries, communities, and digital ecosystem. It serves as the backbone of </span><span class=\"cf0\">Oxagon&rsquo;s</span><span class=\"cf0\"> transformation into a fully connected, cognitive city and plays a vital role in realizing NEOM&rsquo;s vision of intelligent and sustainable urban living.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team conducted comprehensive design reviews spanning from the design delivery stage to Shop drawing stage, ensuring adherence to regulatory and operational standards.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 3, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 11:58:28', '2025-12-15 13:24:40'),
(19, 4, 'HMC Heart Hospital, Doha', 'hmc-heart-hospital', 'HMC Heart Hospital is a specialist tertiary hospital committed to delivering high quality care in cardiology and cardiothoracic surgery for the adult population of Qatar. Located within Hamad Medical Corporation\'s Doha campus, the 116-bed Heart Hospital encompasses a beautiful, spacious and modern facility designed to ensure that we deliver the safest, most effective and compassionate care to each and every patients.', 'Qatar', 'uploads/images/Healthcare_3.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">HMC HEART HOSPITAL, DOHA</span></strong></h3>\r\n<p class=\"pf0\"><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">QATAR </span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\">\r\n<figure class=\"image\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Healthcare_3.png\" alt=\"\" width=\"100%\">\r\n<figcaption></figcaption>\r\n</figure>\r\n</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(0, 0, 0);\">Client</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Hamad Medical Corporation (HMC), Qatar </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(0, 0, 0);\">Sector</span></strong></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Healthcare, Hospital</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire Protection Systems</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Security System Upgrades</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The HMC HEART HOSPITAL in Qatar required a fire and life safety system and security upgrade for its existing facilities. Appointed as an independent consultant by Hamad Medical Corporation to conduct a peer review of various submissions related to the fire and life safety systems and security enhancements.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Conducted an independent peer review of the fire and life safety system and security upgrades for the existing hospital. Reviewed various submissions to ensure compliance with fire safety codes and regulations. Provided recommendations to improve the safety systems and ensure optimal integration with existing infrastructure. The involvement provided independent validation of the fire and life safety systems, ensuring that the HMC Heart Hospital met all necessary standards for safety and security.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/Tunnel_4.jpg,uploads/images/Tunnel_2.png,uploads/images/Tunnel_1.png', 5, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 12:11:43', '2025-12-15 13:04:32'),
(20, 8, 'Time Travel Tunnel, Trojena', 'time-travel-tunnel-trojena', 'The TTT and the Funicular Railway System are the main transportation modes that connect the Mirage passenger terminal, the base of Trojena, with the Vault, the portal to Trojena. ', 'NEOM, KSA', 'uploads/images/Mass_Transit_1.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">TIME TRAVEL TUNNEL, TROJENA</span></strong></h3>\r\n<p><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">NEOM, KINGDOM OF SAUDI ARABIA</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mass_Transit_1.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Mass Transit, Rail Transport</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Performance Based Design (CFD + Egress Modelling)</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Tunnel Ventilation &amp; Tunnel FLS Strategy</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The TTT and the Funicular Railway System are the main transportation modes that connect the Mirage passenger terminal, the base of </span><span class=\"cf0\">Trojena</span><span class=\"cf0\">, with the Vault, the portal to </span><span class=\"cf0\">Trojena</span><span class=\"cf0\">. The system has provision for passengers and freight connects an elevation difference of 1 km over a 4.4 km stretch. Each track has two funiculars that move up and down the slope. The funiculars have two modules, each with three cabins. The funiculars are powered by a drive motor and gearbox located at the Vault and the Mirage station.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team provided design reviews from concept to detailed design, ensuring compliance with safety and regulatory standards. This included reviewing the plant design, FERA Study report, Assumption Register, QRA Study Report etc.</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/40.jpg,uploads/images/Mass_Transit_4.jpg,uploads/images/Mass_Transit_2.jpg,uploads/images/Mass_Transit_3.jpg,uploads/images/Mass_Transit_Sector.jpg,uploads/images/Mass_Transit_5_jpg.png,uploads/images/Mass_Transit_1.jpg', 8, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 12:29:57', '2025-12-15 13:04:38'),
(21, 9, 'Marry Brown Outlet, Terminal 1', 'marry-brown-outlet', 'One of the world\'s largest food chain outlet delivering great value products, a family oriented quick service restaurant.', 'Dubai International Airport, UAE', 'uploads/images/Fitout_10.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">MARRY BROWN OUTLET </span></strong></h3>\r\n<p class=\"pf0\"><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">DUBAI INTERNATIONAL AIRPORT </span><span class=\"cf1\">&ndash; TERMINAL 1</span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Fitout_10.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Al Abbas Interior Environment LLC</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Airport Retail, Commercial</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Architectural Code Consulting</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Design Approval</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Inspections &amp; Compliance Checks</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">System Testing &amp; Commissioning</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Handover Inspections</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Appointed by Al Abbas Interior Environment LLC to provide fire and life safety consulting for the Marry Brown outlet at Terminal 1, Dubai Airport. The scope of work involved ensuring compliance with fire safety regulations through architectural code consulting, conducting inspections, and performing testing and commissioning of the fire and life safety systems.&nbsp;</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Provided architectural code consulting and design approval to ensure compliance with fire safety standards. Conducted inspections to verify that the construction complied with the approved AFS drawings. Performed testing and commissioning of fire and life safety systems to ensure functionality and compliance. Collaborated with the DA </span><span class=\"cf0\">Fitout</span><span class=\"cf0\"> and DA AFS teams during handover inspections to finalize the project. The involvement ensured that the Marry Brown outlet at Terminal 1, Dubai Airport met all essential fire and life safety standards, providing a secure and compliant environment for customers and staff.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', 'uploads/images/Mass_Transit_1.jpg,uploads/images/Fitout_19.png,uploads/images/Fitout_21.jpg,uploads/images/Fitout_17.png,uploads/images/Fitout_16.jpg,uploads/images/Fitout_18.jpg', 9, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 12:41:15', '2025-12-15 13:04:41'),
(22, 5, 'Oxagon Residential Village', 'oxagon-residential-village', 'The OXAGON Village Residential Units are designed to embody the principles of community living and environmental harmony, aligning with NEOM\'s vision for modern, sustainable living. Located across OXAGON island, this mixed-use development aims to create a thriving, diverse community with both residential and commercial spaces. ', 'NEOM, KSA', 'uploads/images/Res-1.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">OXAGON VILLAGE RESIDENTIAL ASSETS</span></strong></h3>\r\n<p class=\"pf0\"><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Res-1.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">High-rise &amp; Mid-Rise residential building</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The OXAGON Village Residential Units are designed to embody the principles of community living and environmental harmony, aligning with NEOM&rsquo;s vision for modern, sustainable living. Located across OXAGON island, this mixed-use development aims to create a thriving, diverse community with both residential and commercial spaces. The project includes 60 residential buildings with a total built-up area of approximately 1,219,400 sqm. These buildings feature seven residential typologies that include apartments, townhouses, and beach houses, offering configurations from one to five bedrooms. The development also provides supporting amenities, commercial spaces, retail outlets, and Food &amp; Beverage (F&amp;B) facilities.</span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The residential buildings are mainly low-rise, apart from 13 high-rise urban buildings. In total, the village will include coastal and urban buildings, with up to six floors per building depending on the typology. The project also features logistic hubs and vehicular storage in the basements of specific typologies. The OXAGON Village aims to become a global model for integrated living and working environments.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Served as the Authority Having Jurisdiction (AHJ) and conducts design reviews from concept to detailed design stage of the asset.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 6, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 12:47:55', '2025-12-15 13:04:34'),
(23, 7, 'The Bow, Trojena', 'the-bow-trojena', 'The Bow project represents a monumental achievement in visionary architectural design and innovative engineering. Departing from conventional dam design, the concept features an inverted dam wall, creating a sculptural vessel seamlessly integrated into a picturesque valley within a majestic mountain setting. ', 'NEOM, KSA', 'uploads/images/Hospitality_6.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">THE BOW, TROJENA</span></strong></h3>\r\n<p><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Hospitality_6.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Highrise underground building of 120m height </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Performance Based Design (CFD)</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Tunnel Ventilation &amp; Tunnel FLS Strategy</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Active Fire System Engineering</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The Bow project represents a monumental achievement in visionary architectural design and innovative engineering. Departing from conventional dam design, the concept features an inverted dam wall, creating a sculptural vessel seamlessly integrated into a picturesque valley within a majestic mountain setting. This iconic development aspires to become an internationally recognized destination for leisure and hospitality. Spanning approximately 300 meters in length, 250 meters in width, and 145 meters in height, The Bow is a massive structure, and its size presents significant constructability challenges. These challenges must be continually evaluated and addressed throughout the design process. A detailed CFD study was conducted and reviewed for fire modeling to demonstrate that tenable conditions are met in the atrium. Additionally, a smoke management system has been proposed to ensure compliance with the requirements.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Served as the Authority Having Jurisdiction (AHJ) and conducts design reviews from concept to detailed design stage of the asset.</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 7, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 12:56:17', '2025-12-15 13:27:44'),
(24, 11, 'Port of Neom', 'port-of-neom', 'The Port of NEOM is being developed as a state-of-the-art logistics and maritime gateway within NEOM Industrial City. Located on the Red Sea, the port is designed to support NEOM\'s long-term industrial, commercial, and construction activities. ', 'NEOM, KSA', 'uploads/images/Marina_2.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">PORT OF NEOM</span></strong></h3>\r\n<p><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Marina_2.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\">&nbsp;</div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA </span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Marina</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The Port of NEOM is being developed as a state-of-the-art logistics and maritime gateway within NEOM Industrial City. Located on the Red Sea, the port is designed to support NEOM&rsquo;s long-term industrial, commercial, and construction activities. The facility features advanced infrastructure including deep-water berths, automated ship-to-shore cranes, electric yard equipment, and modern cargo-handling systems. Built to operate on renewable energy, the port will enable efficient, sustainable movement of containers, general cargo, and project materials. Its strategic location along major global shipping routes positions it as a key logistics hub for regional and international trade. The development supports NEOM&rsquo;s vision for smart, green, and highly automated industrial operations, with the capacity for future expansion as the city grows.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the Authority Having Jurisdiction (AHJ), the team conducted design reviews from concept through detailed design of the Fire and Life Safety (FLS) strategy for the entire facility, ensuring compliance with Saudi Building Codes and relevant international standards. The review encompassed the port&rsquo;s architectural layouts, operational arrangements, fire protection and detection systems, hazard assessments, marine safety provisions, and supporting technical studies. Key documents, including the fire strategy, risk assessments, design basis reports, and engineering submissions, were examined to confirm that the port meets both NEOM requirements and international safety standards.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 11, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 13:00:19', '2025-12-15 13:26:33'),
(25, 12, 'Neom Bioreactor Park-Fermentation Facility', 'neom-bioreactor-park-fermentation-facility', 'The fermentation pilot facility is as a Contract Development and Manufacturing Organization (CDMO) allowing start-ups and larger companies access to the latest bioreactor-based technologies to develop new food ingredients including, but not restricted to, proteins, flavors, fragrances, lipids and colors.', 'NEOM, KSA', 'uploads/images/Industrial_3.png', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">NEOM BIOREACTOR PARK - FERMENTATION FACILITY </span></strong></h3>\r\n<span class=\"cf0\"><span style=\"color: rgb(52, 73, 94);\"><strong>NEOM, KINGDOM OF SAUDI ARABIA</strong></span> </span>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Industrial_3.png\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Industrial and Food sector</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">The fermentation pilot facility will be developed as a Contract Development and Manufacturing Organization (CDMO) allowing start-ups and larger companies access to the latest bioreactor-based technologies to develop new food ingredients including, but not restricted to, proteins, flavors, fragrances, lipids and colors.</span></p>\r\n<p class=\"pf0\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">As the AHJ, the scope of services includes reviewing design drawings for compliance with NFPA, SBC, and related fire safety codes. Key tasks involve defining occupancy classifications, verifying fire resistance ratings, and assessing means of egress for all buildings. Fire protection systems, including alarms, sprinklers, and fire-rated barriers, will be specified as required. Deliverables include detailed fire strategy documents covering zoning, egress plans, and compartmentation.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 12, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 13:04:22', '2025-12-15 13:26:46'),
(26, 6, 'Vault, Trogena', 'vault-trogena', 'NEOM Trojena â Vault: The gateway to Trojena, carved into a man-made canyon, is envisioned as a hyper-modern vertical village (combined high rise & embedded structure) within the mountain region. It features a cantilever structure spanning approximately 784 meters in length, with a maximum roof span of 305 meters.', 'NEOM, KSA', 'uploads/images/Mixed_Use_2.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">VAULT-TROJENA</span></strong></h3>\r\n<p class=\"pf0\"><span style=\"color: rgb(52, 73, 94);\"><strong><span class=\"cf0\">NEOM, KINGDOM OF SAUDI ARABIA </span></strong></span></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mixed_Use_2.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">NEOM, KSA</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Vertical Village (Mixed use)</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">AHJ Services - Fire &amp; Life Safety Engineering Peer Review</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">NEOM </span><span class=\"cf0\">Trojena</span> <span class=\"cf1\">&ndash; Vault: The gateway to </span><span class=\"cf1\">Trojena</span><span class=\"cf1\">, carved into a man-made canyon, is envisioned as a hyper-modern vertical village (combined high rise &amp; embedded structure) within the mountain region. It features a cantilever structure spanning approximately 784 meters in length, with a maximum roof span of 305 meters.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf1\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf1\">Served as the Authority Having Jurisdiction (AHJ) and conducts design reviews from pre concept to design delivery stage of the asset. Performance-based design (CFD) was reviewed and approved for smoke control and penetrations within the Time Travel Tunnel and upper levels, where conventional separations could not be maintained due to the building\'s unique structural and operational requirements. Vertical louvers were designed in compliance with NFPA 204 standards. Additionally, owing to the building\'s distinctive perimeter structure, conventional access coverage for firefighting could not be achieved. As an alternative solution, an in-house fire station and Hydrants with augmented pressure were proposed and approved, ensuring compliance with SBC 801 and NFPA 24 standards for fire safety</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 13, 1, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 13:12:17', '2025-12-15 13:16:16'),
(27, 6, 'Y Tower, Abudhabi', 'y-tower-abudhabi', 'Y Tower is a mixed-use high-rise development in Abu Dhabi, consisting of commercial spaces, a hotel, and residential apartments. The building\'s complex design required detailed fire and life safety strategies to ensure the safety of occupants in both residential and commercial areas. ', 'Abudhabi, UAE', 'uploads/images/Mixed_Use_4.jpg', '<div class=\"container py-4\">\r\n<div class=\"row align-items-center\">\r\n<div class=\"col-md-6\">\r\n<h3><!--StartFragment --></h3>\r\n<h3 class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(22, 145, 121);\">Y TOWER</span></strong></h3>\r\n<p class=\"pf0\"><strong><span class=\"cf0\" style=\"color: rgb(52, 73, 94);\">ABUDHABI, UAE</span></strong></p>\r\n<h3><!--EndFragment --></h3>\r\n</div>\r\n<div class=\"col-md-6\"><img class=\"img-fluid rounded\" src=\"https://admin.xeluxefiresafety.com/uploads/images/Mixed_Use_4.jpg\" alt=\"\" width=\"100%\"></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Client</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">ADNEC</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Sector</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Mixed-Use Highrise Development</span></p>\r\n<p class=\"pf0\">&nbsp;</p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Key Services</span></strong></span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Fire &amp; Life Safety Design</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">CFD Analysis</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Evacuation Modeling</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Peer Review</span></p>\r\n<p class=\"pf0\"><span class=\"cf0\">Performance-Based Design</span></p>\r\n<!--EndFragment --></div>\r\n<div class=\"col-md-6\"><!--StartFragment -->\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Y-Tower is a mixed-use high-rise development in Abu Dhabi, consisting of commercial spaces, a hotel, and residential apartments. The building&rsquo;s complex design required detailed fire and life safety strategies to ensure the safety of occupants in both residential and commercial areas. Appointed as the Fire and Life Safety (FLS) Design Consultant to conduct a peer review of the Performance-Based Analysis submission, using CFD and Evacuation Modeling to assess the fire safety performance of the tower.</span></p>\r\n<p class=\"pf0\"><span style=\"color: rgb(0, 0, 0);\"><strong><span class=\"cf0\">Scope of work&nbsp;</span></strong></span></p>\r\n<p class=\"pf0\" style=\"text-align: justify;\"><span class=\"cf0\">Conducted peer review of the Performance-Based Analysis for the fire safety systems. Assessed the effectiveness of the design using CFD analysis and Evacuation Modeling. Provided feedback and recommendations to ensure compliance with relevant fire safety codes and standards. The involvement ensured that the fire safety strategy for the Y-Tower met the highest standards, optimizing the design for both safety and regulatory compliance.</span></p>\r\n<!--EndFragment --></div>\r\n</div>\r\n</div>', '', 13, 0, 1, NULL, NULL, 'index,follow', NULL, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'Article', NULL, NULL, NULL, NULL, NULL, NULL, 1, 0.5, 'monthly', '2025-12-15 13:16:06', '2025-12-15 13:26:53');

