INSERT INTO `cms_about_us` (`id`, `page_title`, `page_subtitle`, `quote_text`, `quote_subtext`, `about_content`, `updated_at`) VALUES
	(1, 'Who We Are', 'Our We partner with clients to engineer safer, better-performing buildings and infrastructure — guiding them from concept to handover and beyond.', 'Xeluxe is a specialist engineering consultancy.', 'We partner with clients to ensure fire and life safety, enhance operational performance, and improve sustainability across the built environment.', '<p>Founded in 2014 and headquartered in Dubai &mdash; with offices in Abu Dhabi, Dammam, India, and Ireland &mdash; we operate across the EMEAI region and are trusted by clients in&nbsp;<strong>aviation</strong>,&nbsp;<strong>hospitality</strong>,&nbsp;<strong>giga projects</strong>,&nbsp;<strong>infrastructure</strong>, and&nbsp;<strong>industrial development</strong>.</p>\r\n<p>Our expertise spans&nbsp;<strong>Fire &amp; Life Safety</strong>,&nbsp;<strong>Process Safety</strong>,&nbsp;<strong>Engineering Simulations</strong>,&nbsp;<strong>Acoustics</strong>,&nbsp;<strong>Commissioning</strong>,&nbsp;<strong>Construction Supervision with Integrated Peer Review</strong>&nbsp;&mdash; supporting clients from concept through to handover and ongoing operations.</p>\r\n<p>We are&nbsp;<strong>ISO 9001</strong>&nbsp;certified and officially recognised as a&nbsp;<strong>House of Expertise by Dubai Civil Defence</strong>, with advanced-stage approvals in progress with Abu Dhabi and Saudi Civil Defence (Salamah).</p>\r\n<p>Our team of 50+ certified professionals &mdash; including&nbsp;<strong>CEng</strong>,&nbsp;<strong>PE</strong>,&nbsp;<strong>PhD</strong>, and&nbsp;<strong>CFPS</strong> &mdash; brings regulatory insight and real-world project experience to deliver practical, code-compliant solutions.</p>', '2025-11-18 14:46:38');
