	(1, 'home', 'Home Page', 'Xeluxe - Fire & Life Safety Engineering Consultants', 'Leading fire and life safety engineering consultants providing innovative solutions for hospitality, healthcare, residential, commercial, and infrastructure projects across the Middle East and beyond.', 'fire safety engineering, life safety consulting, fire protection design, building code compliance, performance-based design, fire engineering consultants', NULL, 1, 1, 'Xeluxe - Fire & Life Safety Engineering Excellence', 'Delivering world-class fire and life safety engineering solutions for complex developments. Expert consultants in master planning, code compliance, and performance-based design.', NULL, 'website', 'summary_large_image', 'Xeluxe - Fire & Life Safety Engineering', 'Leading fire and life safety engineering consultants providing innovative solutions for hospitality, healthcare, residential, and infrastructure projects.', NULL, 'WebSite', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(2, 'services', 'Services Listing', 'Fire & Life Safety Services - Xeluxe', 'Comprehensive fire and life safety engineering services including master planning, code compliance, performance-based design, egress analysis, smoke control, and commissioning for all project types.', 'fire safety services, life safety consulting, code compliance, egress design, smoke control, fire protection systems, commissioning services', NULL, 1, 1, 'Fire & Life Safety Engineering Services', 'Expert fire and life safety services for master planning, code consulting, performance-based design, egress analysis, and system commissioning.', NULL, 'website', 'summary_large_image', 'Fire & Life Safety Services - Xeluxe', 'Comprehensive engineering services for fire protection, life safety compliance, and emergency response planning across all sectors.', NULL, 'CollectionPage', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(3, 'sectors', 'Project Sectors - Xeluxe', 'Fire & Life Safety Projects Across All Sectors - Xeluxe', 'View our portfolio across hospitality, healthcare, residential, commercial, aviation, mass transit, and industrial sectors. Delivering fire and life safety excellence for complex developments worldwide.', 'project sectors, fire safety projects, hospitality fire engineering, healthcare safety, residential projects, commercial developments, aviation safety, mass transit fire protection', NULL, 1, 1, 'Fire & Life Safety Project Sectors', 'Explore our diverse portfolio of fire and life safety projects across hospitality, healthcare, residential, commercial, aviation, and infrastructure sectors.', NULL, 'website', 'summary_large_image', 'Project Sectors - Xeluxe', 'View our successful fire and life safety engineering projects across multiple sectors including hospitality, healthcare, and infrastructure.', NULL, 'CollectionPage', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(4, 'blog', 'Insights & Articles - Xeluxe', 'Fire & Life Safety Insights & Articles - Xeluxe', 'Latest insights, technical articles, and industry knowledge on fire and life safety engineering, building codes, performance-based design methodologies, and project case studies from our expert consultants.', 'fire safety insights, engineering articles, building codes, fire protection standards, NFPA, life safety insights, performance-based design, fire engineering blog', NULL, 1, 1, 'Fire & Life Safety Insights & Articles', 'Expert insights and technical articles on fire and life safety engineering, code updates, industry trends, and innovative design solutions.', NULL, 'website', 'summary_large_image', 'Fire Safety Insights - Xeluxe', 'Latest articles on fire and life safety engineering, building codes, performance-based design, and project case studies from industry experts.', NULL, 'Blog', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(5, 'careers', 'Careers at Xeluxe', 'Careers - Join Xeluxe Fire & Life Safety Team', 'Join our team of fire and life safety engineering professionals. Explore career opportunities in fire protection design, code consulting, performance-based analysis, and project management across the Middle East.', 'fire safety careers, engineering jobs, fire protection careers, life safety jobs, engineering consultancy careers, Middle East jobs', NULL, 1, 1, 'Join Xeluxe - Fire & Life Safety Careers', 'Build your career with a leading fire and life safety engineering consultancy. Opportunities for engineers, designers, and technical specialists.', NULL, 'website', 'summary_large_image', 'Careers at Xeluxe', 'Join our team of fire and life safety engineering professionals. Explore exciting career opportunities in innovative engineering consultancy.', NULL, 'WebPage', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(6, 'contact-us', 'Contact Xeluxe', 'Contact Xeluxe - Fire & Life Safety Consultants', 'Get in touch with our fire and life safety engineering experts. Contact us for project inquiries, technical consultations, and professional fire protection engineering services.', 'contact fire safety consultants, fire engineering inquiries, life safety consultation, project inquiries, engineering services contact', NULL, 1, 1, 'Contact Xeluxe - Fire & Life Safety Consultants', 'Reach out to our team of fire and life safety engineering experts for project inquiries, technical consultations, and professional services.', NULL, 'website', 'summary_large_image', 'Contact Xeluxe', 'Get in touch with our fire and life safety engineering consultants for inquiries, consultations, and professional services.', NULL, 'ContactPage', NULL, NULL, NULL, '2025-11-19 07:30:12', '2025-11-21 19:00:00'),
	(7, 'about-us', 'About Xeluxe', 'About Xeluxe - Fire & Life Safety Engineering Experts', 'About Xeluxe - Leading fire and life safety engineering consultants with global expertise in hospitality, healthcare, residential, commercial, and infrastructure projects. Our vision, mission, values, and expert team.', 'about Xeluxe, fire safety consultants, engineering company, our vision, our mission, our values, fire engineering team, life safety experts, Middle East consultancy', '', 1, 1, 'About Xeluxe - Fire & Life Safety Engineering Excellence', 'Learn about our company, vision, mission, core values, and meet our team of fire and life safety engineering experts delivering innovative solutions worldwide.', '', 'website', 'summary_large_image', 'About Xeluxe', 'Discover our story, vision, mission, and values. Meet the team delivering world-class fire and life safety engineering solutions.', '', 'AboutPage', '', '', '', '2025-11-21 07:00:00', '2025-11-24 06:37:27'),
	(8, 'service/process-safety', 'Process Safety', 'Process Safety', 'Our Process Safety expertise spans a diverse range of disciplines, including technical HSE, process safety management, hazard identification and analysis, loss prevention strategies, risk assessment and mitigation, and compliance with international safety standards. ', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:06', '2025-11-22 05:20:06'),
	(9, 'service/engineering-simulations', 'Engineering Simulations', 'Engineering Simulations', 'Our design team has extensive experience in performance-based options with major architects and in iconic projects and has resolved limitations posed by code requirements by effectively justifying that the code intent is met by achieving specific fire and life safety goals.    \r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:09', '2025-11-22 05:20:09'),
	(10, 'service/acoustics', 'Acoustics', 'Acoustics', 'Our Acoustics services focus on creating sound environments that enhance occupant comfort. By addressing both architectural and mechanical acoustics, we ensure that spaces are designed not only for functionality but also for optimal acoustic performance, contributing to overall well-being and satisfaction.', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:15', '2025-11-22 05:20:15'),
	(11, 'service/construction-supervision', 'Construction Supervision', 'Construction Supervision', 'We provide comprehensive construction supervision services across architecture, structure, infrastructure, MEP, FLS and ICT. Our role is to ensure that every phase of construction aligns with the approved design, technical standards, and contractual requirements.\r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:19', '2025-11-22 05:20:19'),
	(12, 'service/commissioning', 'Commissioning', 'Commissioning', 'Our team ensures that building systems - particularly MEP and Fire Life Safety (FLS) - are designed, installed, tested, and performing in accordance with the design intent. At its core, commissioning is a process of communication, documentation, and verification - bringing together stakeholders to ensure the building operates as it was envisioned.\r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:24', '2025-11-22 05:20:24'),
	(13, 'service/master-planning-fire-life-safety', 'Master Planning – Fire & Life Safety', 'Master Planning – Fire & Life Safety', 'Integration of Fire and Life Safety principles into urban planning and development of large-scale infrastructure projects is essential for embedding robust emergency response strategies, resilience and efficiency within the overall design and functionality of the development.', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:29', '2025-11-22 05:20:29'),
	(14, 'service/fire-life-safety-code-compliant-performance-based-design', 'Fire & Life Safety – Code Compliant & Performance Based design', 'Fire & Life Safety – Code Compliant & Performance Based design', 'Our Fire Safety expertise ranges from assuring compliance with prescriptive code-based requirements as well as performance-based assessments and covers broad range of sectors including Hospitality, Aviation, Commercial, Industrial, high-rise developments etc.', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:35', '2025-11-22 05:20:35'),
	(15, 'service/fire-life-safety-system-design', 'Fire & Life Safety System Design', 'Fire & Life Safety System Design', 'At Xeluxe, we believe that effective Fire & Life Safety design is the foundation of resilient and sustainable built environments. Our approach goes beyond compliance — we integrate advanced fire protection, detection, and life safety systems seamlessly into the design process to enhance safety, functionality, and operational continuity. ', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:41', '2025-11-22 05:20:41'),
	(16, 'service/peer-review-and-authority-representation', 'Peer Review and Authority Representation', 'Peer Review and Authority Representation', 'Our experienced team has engaged in Mega projects across the regions and is versatile in developing Fire and Life safety strategies as per international standards viz International Building Code, International Fire Code, NFPA standards, British Standards - Approved Document B, BS 9999, BS 7974, along with local codes such as Saudi Building code, UAE Fire and Life Safety Code, and GCC Codes. \r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:47', '2025-11-22 05:20:47'),
	(17, 'service/risk-assessment-hazardous-materials-storage-process', 'Risk Assessment - Hazardous Materials Storage & Process', 'Risk Assessment - Hazardous Materials Storage & Process', 'Hazardous materials whether stored, processed or transported, present unique fire and life-safety challenges. Our risk assessments evaluate the storage conditions, process flows and potential failure modes to protect people, property and operations. \r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:50', '2025-11-22 05:20:50'),
	(18, 'service/retrospective-assessment-of-existing-facilities-risk-assessments', 'Retrospective Assessment of Existing Facilities/ Risk Assessments', 'Retrospective Assessment of Existing Facilities/ Risk Assessments', 'We understand the need for a risk-based approach to be followed for existing buildings where prescriptive compliance may not be the norm always. Our Company has undertaken retrospective assessment of key assets within the region to ensure that the facility remains resilient and assure business continuity. \r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:20:58', '2025-11-22 05:20:58'),
	(19, 'service/fire-life-safety-construction-supervision-testing-commissioning', 'Fire & Life Safety - Construction Supervision, Testing & Commissioning', 'Fire & Life Safety - Construction Supervision, Testing & Commissioning', 'Xeluxe Consultants specializes in construction management across all MEP systems, offering testing and commissioning services for fire and life safety, HVAC, electrical, and plumbing systems. This systematic approach guarantees operational excellence, legal compliance, and the long-term resilience of buildings.', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:01', '2025-11-22 05:21:01'),
	(20, 'service/emergency-response-planning', 'Emergency Response Planning', 'Emergency Response Planning', 'We develop comprehensive emergency preparedness plans that equip organizations with the tools, training, and strategies needed to respond swiftly, minimize disruption, and restore operations with minimal impact.\r\n', NULL, NULL, 1, 1, NULL, NULL, NULL, 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:05', '2025-11-22 05:21:05'),
	(21, 'project/industrial-storage', 'Industrial & Storage', 'Industrial & Storage', 'Industrial plants, warehouses, logistics hubs, hazardous material storage, and manufacturing facilities present high-risk environments due to large fuel loads, high fire growth potential, combustible materials, and critical operational processes. Xeluxe delivers specialized Fire & Life Safety solutions for industrial and storage facilities, focusing on risk-based engineering to address hazards associated with flammable materials, high fuel loads, and complex storage configurations. ', NULL, NULL, 1, 1, 'Industrial & Storage', 'Industrial plants, warehouses, logistics hubs, hazardous material storage, and manufacturing facilities present high-risk environments due to large fuel loads, high fire growth potential, combustible materials, and critical operational processes. Xeluxe delivers specialized Fire & Life Safety solutions for industrial and storage facilities, focusing on risk-based engineering to address hazards associated with flammable materials, high fuel loads, and complex storage configurations. ', '', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:27', '2025-11-22 05:21:27'),
	(22, 'project/mission-critical', 'Mission Critical', 'Mission Critical', 'Modern mission-critical facilities such as data centers, control rooms, telecommunication towers, Power generation & control stations, Nuclear energy plants etc. operate in environments where even seconds of downtime can result in significant economic loss, safety risks, or disruption to national operations. At Xeluxe, we deliver specialized Fire & Life Safety engineering solutions that protect mission-critical environments where operational continuity is paramount.', NULL, NULL, 1, 1, 'Mission Critical', 'Modern mission-critical facilities such as data centers, control rooms, telecommunication towers, Power generation & control stations, Nuclear energy plants etc. operate in environments where even seconds of downtime can result in significant economic loss, safety risks, or disruption to national operations. At Xeluxe, we deliver specialized Fire & Life Safety engineering solutions that protect mission-critical environments where operational continuity is paramount.', '', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:30', '2025-11-22 05:21:30'),
	(23, 'project/education', 'Education', 'Education', 'Educational facilities must address complex Fire & Life Safety requirements across classrooms, laboratories, research centers, and assembly spaces, ensuring compliance, resilience and the wellbeing of students, faculty, and visitors. At Xeluxe, we deliver comprehensive Fire & Life Safety engineering for the education sector, offering risk assessments, code consulting, performance-based design and tailored safety strategies that emphasize accessibility, resilience and ongoing operational safety. ', NULL, NULL, 1, 1, 'Education', 'Educational facilities must address complex Fire & Life Safety requirements across classrooms, laboratories, research centers, and assembly spaces, ensuring compliance, resilience and the wellbeing of students, faculty, and visitors. At Xeluxe, we deliver comprehensive Fire & Life Safety engineering for the education sector, offering risk assessments, code consulting, performance-based design and tailored safety strategies that emphasize accessibility, resilience and ongoing operational safety. ', 'uploads/images/32.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:35', '2025-11-22 05:21:35'),
	(24, 'project/aviation', 'Aviation', 'Aviation', 'Airports, aircraft hangars, maintenance facilities, cargo terminals, and aviation fuel storage areas present complex fire safety challenges due to high fuel loads, large open volumes, rapid smoke movement, and strict regulatory oversight. Xeluxe provides specialized Fire & Life Safety solutions tailored for the aviation sector, focusing on performance-based design, foam suppression system design, hangar fire risk assessments  ', NULL, NULL, 1, 1, 'Aviation', 'Airports, aircraft hangars, maintenance facilities, cargo terminals, and aviation fuel storage areas present complex fire safety challenges due to high fuel loads, large open volumes, rapid smoke movement, and strict regulatory oversight. Xeluxe provides specialized Fire & Life Safety solutions tailored for the aviation sector, focusing on performance-based design, foam suppression system design, hangar fire risk assessments  ', 'uploads/images/9.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:39', '2025-11-22 05:21:39'),
	(25, 'project/roads-tunnels', 'Roads & Tunnels', 'Roads & Tunnels', 'Xeluxe delivers engineered Fire & Life Safety solutions tailored for complex transport infrastructure, addressing critical risks such as rapid smoke spread, limited firefighting access, structural heat exposure, and emergency response delays. Our expertise includes both risk and performance-based fire engineering, CFD smoke modelling, evacuation analysis, and integrated system design aligned with international standards. ', NULL, NULL, 1, 1, 'Roads & Tunnels', 'Xeluxe delivers engineered Fire & Life Safety solutions tailored for complex transport infrastructure, addressing critical risks such as rapid smoke spread, limited firefighting access, structural heat exposure, and emergency response delays. Our expertise includes both risk and performance-based fire engineering, CFD smoke modelling, evacuation analysis, and integrated system design aligned with international standards. ', 'uploads/images/40.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:45', '2025-11-22 05:21:51'),
	(26, 'project/marina', 'Marina', 'Marina', 'Marinas, yacht clubs, cruise terminals, and waterfront mixed-use developments present a highly specialized fire safety challenge due to the combination of waterfront exposure, high-value assets, fuel storage, and public access areas operating simultaneously. Xeluxe delivers advanced fire engineering and risk management solutions tailored for maritime infrastructure, ensuring compliance with local and international marine fire safety standards.', NULL, NULL, 1, 1, 'Marina', 'Marinas, yacht clubs, cruise terminals, and waterfront mixed-use developments present a highly specialized fire safety challenge due to the combination of waterfront exposure, high-value assets, fuel storage, and public access areas operating simultaneously. Xeluxe delivers advanced fire engineering and risk management solutions tailored for maritime infrastructure, ensuring compliance with local and international marine fire safety standards.', '', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:21:56', '2025-11-22 05:21:56'),
	(27, 'project/fit-out', 'Fit Out', 'Fit Out', 'Fit-out projects within commercial, hospitality, healthcare, and mixed-use developments often involve changes in space usage, interior layout modifications, or tenant-specific enhancements. At Xeluxe, we specialize in delivering fit-out fire safety solutions that seamlessly integrate with the base building strategy, ensuring zero compromise to life safety systems, minimal disruption to existing infrastructure, and full alignment with authority requirements.\r\n', NULL, NULL, 1, 1, 'Fit Out', 'Fit-out projects within commercial, hospitality, healthcare, and mixed-use developments often involve changes in space usage, interior layout modifications, or tenant-specific enhancements. At Xeluxe, we specialize in delivering fit-out fire safety solutions that seamlessly integrate with the base building strategy, ensuring zero compromise to life safety systems, minimal disruption to existing infrastructure, and full alignment with authority requirements.\r\n', '', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:00', '2025-11-22 05:22:00'),
	(28, 'project/commercial-mixed-use-developments', 'Commercial & Mixed Use Developments', 'Commercial & Mixed Use Developments', 'Commercial architecture is increasingly complex and design driven. Aesthetically expressive buildings with open atriums, interconnected spaces, and innovative façades cannot rely solely on standard code-based solutions. We collaborate closely with architects, developers, and operators to deliver prescriptive and performance-based fire engineering solutions that achieve both safety and design excellence.', NULL, NULL, 1, 1, 'Commercial & Mixed Use Developments', 'Commercial architecture is increasingly complex and design driven. Aesthetically expressive buildings with open atriums, interconnected spaces, and innovative façades cannot rely solely on standard code-based solutions. We collaborate closely with architects, developers, and operators to deliver prescriptive and performance-based fire engineering solutions that achieve both safety and design excellence.', 'uploads/images/29.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:04', '2025-11-22 05:22:04'),
	(29, 'project/healthcare', 'Healthcare', 'Healthcare', 'Xeluxe specializes in designing and implementing advanced Fire & Life Safety strategies tailored specifically for healthcare environments, where evacuation complexity, infection control, and continuous clinical operations demand a highly engineered approach.\r\n', NULL, NULL, 1, 1, 'Healthcare', 'Xeluxe specializes in designing and implementing advanced Fire & Life Safety strategies tailored specifically for healthcare environments, where evacuation complexity, infection control, and continuous clinical operations demand a highly engineered approach.\r\n', 'uploads/images/11.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:09', '2025-11-24 07:11:34'),
	(30, 'project/mass-transit', 'Mass Transit', 'Mass Transit', 'Xeluxe deliver engineered Fire & Life Safety solutions designed specifically for mass transit environments, ensuring seamless operations, business continuity, and passenger protection without compromising on innovation, passenger experience, or architectural aesthetics.\r\n', NULL, NULL, 1, 1, 'Mass Transit', 'Xeluxe deliver engineered Fire & Life Safety solutions designed specifically for mass transit environments, ensuring seamless operations, business continuity, and passenger protection without compromising on innovation, passenger experience, or architectural aesthetics.\r\n', 'uploads/images/39.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:14', '2025-11-22 05:22:14'),
	(31, 'project/residential', 'Residential', 'Residential', 'Xeluxe brings world-class fire engineering expertise to ensure that residential developments are safe, resilient, and fully compliant with international and local fire safety codes. Our strategies are designed to meet regulatory requirements and to create safer living environments that protect families, support developer reputation, and enhance long-term asset value.\r\n', NULL, NULL, 1, 1, 'Residential', 'Xeluxe brings world-class fire engineering expertise to ensure that residential developments are safe, resilient, and fully compliant with international and local fire safety codes. Our strategies are designed to meet regulatory requirements and to create safer living environments that protect families, support developer reputation, and enhance long-term asset value.\r\n', 'uploads/images/1.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:22', '2025-11-24 07:11:41'),
	(32, 'project/hospitality', 'Hospitality', 'Hospitality', 'With the global hospitality industry experiencing rapid growth and increasing customer expectations, guest safety and operational continuity have become critical priorities for hotel operators who are strongly focused on protecting their brand reputation. At Xeluxe, we understand these challenges and go beyond compliance to deliver innovative and robust Fire & Life Safety solutions. ', NULL, NULL, 1, 1, 'Hospitality', 'With the global hospitality industry experiencing rapid growth and increasing customer expectations, guest safety and operational continuity have become critical priorities for hotel operators who are strongly focused on protecting their brand reputation. At Xeluxe, we understand these challenges and go beyond compliance to deliver innovative and robust Fire & Life Safety solutions. ', 'uploads/images/14.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:26', '2025-11-22 05:22:26'),
	(33, 'blog-details/sample-test-blog', 'sample test blog', 'sample test blog', 'this is test', NULL, NULL, 1, 1, 'sample test blog', 'this is test', 'uploads/images/29.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:22:55', '2025-11-22 05:22:55'),
	(34, 'career-details/test-career', 'Test Career', 'Test Career', 'Test career short description', NULL, NULL, 1, 1, 'Test Career', 'Test career short description', 'uploads/images/21b.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-22 05:23:02', '2025-11-22 05:23:02'),
	(35, 'sector/education', 'Education', 'Education', '<p><span style="font-size: 11.0pt; font-family: \'Century Gothic\'; mso-ascii-font-family: \'Century Gothic\'; mso-fareast-font-family: Aptos; mso-bidi-font-family: Arial; color: black; mso-color-index: 1; mso-font-kerning: 1.0pt; language: en-US; mso-style-textfill-type: solid; mso-style-textfill-fill-themecolor: text1; mso-style-textfill-fill-color: black; mso-style-textfill-fill-alpha: 100.0%;">At Xeluxe, we deliver comprehensive Fire &amp; Life Safety engineering for the education sector, offering risk assessments, code consulting, performance-based design and tailored safety strategies that emphasize accessibility, resilience and ongoing operational safety. </span></p>', NULL, NULL, 1, 1, 'Education', '<p><span style="font-size: 11.0pt; font-family: \'Century Gothic\'; mso-ascii-font-family: \'Century Gothic\'; mso-fareast-font-family: Aptos; mso-bidi-font-family: Arial; color: black; mso-color-index: 1; mso-font-kerning: 1.0pt; language: en-US; mso-style-textfill-type: solid; mso-style-textfill-fill-themecolor: text1; mso-style-textfill-fill-color: black; mso-style-textfill-fill-alpha: 100.0%;">At Xeluxe, we deliver comprehensive Fire &amp; Life Safety engineering for the education sector, offering risk assessments, code consulting, performance-based design and tailored safety strategies that emphasize accessibility, resilience and ongoing operational safety. </span></p>', 'uploads/images/32.jpg', 'website', 'summary_large_image', NULL, NULL, NULL, 'WebPage', NULL, NULL, NULL, '2025-11-25 17:10:18', '2025-11-25 17:10:18'),
