INSERT INTO `cms_services` (`id`, `category_id`, `title`, `slug`, `short_description`, `content`, `featured_image`, `meta_title`, `meta_description`, `meta_keywords`, `og_title`, `og_description`, `og_image`, `twitter_card`, `twitter_title`, `twitter_description`, `canonical_url`, `robots_index`, `robots_follow`, `display_order`, `is_active`, `created_at`, `updated_at`) VALUES
	(1, 1, 'Master Planning – Fire & Life Safety', 'master-planning-fire-life-safety', 'Integration of Fire and Life Safety principles into urban planning and development of large-scale infrastructure projects is essential for embedding robust emergency response strategies, resilience and efficiency within the overall design and functionality of the development.', '<div class="row">\r\n<div class="col-md-8">\r\n<p>&nbsp;</p>\r\n</div>\r\n<div class="col-md-4">\r\n<p><img style="width: 100%;" src="http://xeluxe-admin.local/uploads/images/27b.jpg" alt=""></p>\r\n</div>\r\n</div>\r\n<div class="container">\r\n<div class="row">\r\n<div class="col-12">\r\n<p style="margin-top: 16px; margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">Integration of Fire and Life Safety principles into urban planning and development of large-scale infrastructure projects is essential for embedding robust emergency response strategies, resilience and efficiency within the overall design and functionality of the development.</span></span></span></span></span></span></span></span></span></span></p>\r\n<p style="margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">Our expert team brings extensive experience in developing and reviewing Fire and Life Safety Master Plan strategies, focusing on:</span></span></span></span></span></span></span></span></span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="container py-4">\r\n<div class="row align-items-center">\r\n<div class="col-md-6">\r\n<div class="O0" style="margin-left: 36px; text-indent: -.38in; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="color: #ffc000;"><span style="font-family: \'Courier New\';">+</span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;"><span style="font-weight: bold;">Strategic Placement and sizing of Fire Stations</span></span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">: To ensure optimal locations for fire stations to maximize coverage for all assets and minimize response times while ensuring optimistic deployment of Emergency response personnel in the distribution as per requirements of NFPA 1710, Community Risk assessment and integrating fire safety resilience in the process.</span></span></span></span></span></span></span></span></span></span></div>\r\n<div style="margin-left: 36px; text-indent: -.38in; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="color: #ffc000;"><span style="font-family: \'Courier New\';">+</span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;"><span style="font-weight: bold;">Sitewide Road Network Design</span></span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">:</span></span></span></span> <span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">Planning efficient road layouts to facilitate seamless access for emergency vehicles and improve overall mobility while confirming to prescriptive requirements of the governing international and local building codes including NFPA 1, IBC, SBC, UAE Fire and Life Safety Code etc. Establishing alternate means of compliance by integrating suited Emergency Response Strategy to preserve the design intent and functionali</span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">ty </span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">of the development while achieving resilient Emergency response measures. </span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">We shape the efficiency of the master plan </span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">by </span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;">addressing the challenges posed by high-rise &amp; underground buildings, tunnels and bridges within a master plan by ensuring the proper Emergency access for these unique structures.</span></span></span></span></span></span></span></span></span></span></div>\r\n<div class="O0" style="margin-left: 36px; text-indent: -.38in; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="color: #ffc000;"><span style="font-family: \'Courier New\';">+</span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;"><span style="font-weight: bold;">Communication Strategy</span></span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">: Developing efficient Communication strategy to integrate assets with Emergency control centers and public safety answering points to enhance response duration, real time monitoring, multiple agency coordination, managing evacuation and increase survivability. </span></span></span></span></span></span></span></span></span></span></div>\r\n<div class="O0" style="margin-left: 36px; text-indent: -.38in; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 12.0pt;"><span style="color: #ffc000;"><span style="font-family: \'Courier New\';">+</span></span></span><span style="font-size: 12.0pt;"><span style="font-family: Aptos;"><span style="color: black;"><span style="language: en-GB;"><span style="font-weight: bold;">Efficient plot allocation, Building Separation and Fire Spread Control</span></span></span></span></span><span style="font-size: 12.0pt;"><span style="font-family: Aptos;"><span style="color: black;"><span style="language: en-GB;">: E</span></span></span></span><span style="font-size: 12.0pt;"><span style="font-family: Aptos;"><span style="color: black;"><span style="language: en-GB;">stablishing adequate spatial separation between buildings to prevent or minimize the risk of fire spread from one structure to another, thereby enhancing overall site fire safety and resilience.</span></span></span></span></span></span></span></span></span></span></div>\r\n<div class="O0" style="margin-left: 36px; text-indent: -.38in; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="color: #ffc000;"><span style="font-family: \'Courier New\';">+</span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;"><span style="font-weight: bold;">Fire Water Supply Systems</span></span></span></span></span><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">:</span></span></span></span> <span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">Designing reliable and accessible fire water infrastructure to support emergency firefighting operations.</span></span></span></span></span></span></span></span></span></span></div>\r\n</div>\r\n<div class="col-md-6"><img class="img-fluid rounded" src="../uploads/images/2.jpg" alt="" width="100%"></div>\r\n</div>\r\n</div>\r\n<div class="container">\r\n<div class="row">\r\n<div class="col-12">\r\n<p><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-US;">These strategic decisions are made in the broader context of the development to balance safety and operational efficiency with cost-effectiveness. By integrating these considerations early in the planning process, we help optimize budgets while maintaining the essential delivery of emergency response services, safeguarding both life and property.</span></span></span></span></p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/36.jpg', 'Services | Master Planning – Fire & Life Safety', '', '', '', '', '', 'summary_large_image', '', '', '', 1, 1, 1, 1, '2025-11-18 06:52:06', '2025-11-21 02:08:26'),
	(2, 1, 'Fire & Life Safety – Code Compliant & Performance Based design', 'fire-life-safety-code-compliant-performance-based-design', 'Our Fire Safety expertise ranges from assuring compliance with prescriptive code-based requirements as well as performance-based assessments and covers broad range of sectors including Hospitality, Aviation, Commercial, Industrial, high-rise developments etc.', '<div class="row">\r\n<div class="col-md-8">&nbsp;</div>\r\n<div class="col-md-4">&nbsp;</div>\r\n<div class="row">\r\n<div class="col-md-3">&nbsp;</div>\r\n<div class="col-md-3">&nbsp;</div>\r\n<div class="col-md-3">&nbsp;</div>\r\n<div class="col-md-3">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class="row">\r\n<div class="col-md-8">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n<div class="container">\r\n<div class="row">\r\n<div class="col-12">\r\n<h4 style="margin-top: 16px; margin-bottom: 11px; text-align: justify;">Fire &amp; Life Safety &ndash; Code Compliant &amp; Performance Based design</h4>\r\n<h4 style="margin-top: 16px; margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;">Our Fire Safety expertise ranges from assuring compliance with prescriptive code-based requirements as well as performance-based assessments and covers broad range of sectors including Hospitality, Aviation, Commercial, Industrial, high-rise developments etc.&nbsp;</span></span></span></span></span></span></span></span></span></span></h4>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="container py-4">\r\n<div class="row align-items-center">\r\n<div class="col-md-6">\r\n<p style="margin-top: 16px; margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;"><span style="font-weight: bold;">Code Compliant Fire &amp; Life Safety Design </span></span></span></span></span></span></span></span></span></span></span></p>\r\n<p style="margin-top: 16px; margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;">Our experienced team has engaged in Mega projects across the regions and is versatile in developing Fire and Life safety strategies as per international standards viz International Building Code, International Fire Code, NFPA standards, British Standards - Approved Document B, BS 9999, BS 7974, along with local codes such as Saudi Building code, UAE Fire and Life Safety Code, and GCC Codes. </span></span></span></span></span></span></span></span></span></span></p>\r\n<p style="margin-top: 16px; margin-bottom: 11px; text-align: justify;"><span style="language: en-IN;"><span style="line-height: 115%;"><span style="text-justify: inter-ideograph;"><span style="unicode-bidi: embed;"><span style="word-break: normal;"><span style="punctuation-wrap: hanging;"><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;">Our holistic approach assists our clients in streamlining the design at the early stages as per code and authority requirements. The Organization places emphasis on providing practical solutions to upkeep the uniqueness of the design while maintaining the intent of the code. </span></span></span></span></span></span></span></span></span></span></p>\r\n<p><span style="font-size: 11.0pt;"><span style="font-family: \'Century Gothic\';"><span style="color: black;"><span style="language: en-GB;">The Organization possess affiliation with UAE and Saudi Civil D&eacute;fense Administrations and are authorized to endorse Fire and Life Safety submissions to the Authority Having jurisdiction.</span></span></span></span></p>\r\n</div>\r\n<div class="col-md-6"><img class="img-fluid rounded" src="../uploads/images/11.jpg" alt="" width="100%"></div>\r\n</div>\r\n</div>\r\n<div class="container">\r\n<div class="row">\r\n<div class="col-md-3">\r\n<p><img src="../uploads/images/1.jpg" alt="" width="100%"></p>\r\n</div>\r\n<div class="col-md-3">\r\n<p><img src="../uploads/images/27b.jpg" alt="" width="100%"></p>\r\n</div>\r\n<div class="col-md-3">\r\n<p><img src="../uploads/images/22a.jpg" alt="" width="100%"></p>\r\n</div>\r\n<div class="col-md-3">\r\n<p><img src="../uploads/images/Marina_1.jpg" alt="" width="100%"></p>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="container py-4">\r\n<div class="row align-items-center">\r\n<div class="col-md-6"><img class="img-fluid rounded" src="../uploads/images/25.jpg" alt="" width="400" height="542"></div>\r\n<div class="col-md-6">\r\n<h5>Performance Based Design&nbsp;</h5>\r\n<p><br>Performance-Based Design offers a flexible and innovative approach to achieving compliance in projects where complex and sophisticated architectural forms extend beyond the limits of prescriptive requirements. At Xeluxe, our Fire &amp; Life Safety team has extensive experience in developing performance-based solutions for iconic projects in collaboration with leading architects. Through advanced Computational Fluid Dynamics (CFD) modelling we assess smoke movement, evacuation performance, and tenability to optimize smoke control systems and justify alternative design approaches. Our expertise extends to egress and crowd movement simulations and performance-based structural fire assessments that enable us to support efficient design while ensuring life safety objectives being achieved and design integrity and operational efficiency met.</p>\r\n</div>\r\n</div>\r\n</div>', 'uploads/images/27a.jpg', 'Services | Fire & Life Safety – Code Compliant & Performance Based design', '', '', '', '', '', 'summary_large_image', '', '', '', 1, 1, 2, 1, '2025-11-18 07:38:52', '2025-11-21 02:17:01'),
	(3, 1, 'Fire & Life Safety System Design', 'fire-life-safety-system-design', 'At Xeluxe, we believe that effective Fire & Life Safety design is the foundation of resilient and sustainable built environments. Our approach goes beyond compliance — we integrate advanced fire protection, detection, and life safety systems seamlessly into the design process to enhance safety, functionality, and operational continuity. ', '<p>&nbsp;</p>\r\n<div class="container">\r\n<div class="row">\r\n<div class="col-12">\r\n<p><span style="font-size: 11.0pt; font-family: \'Century Gothic\'; mso-ascii-font-family: \'Century Gothic\'; mso-fareast-font-family: +mn-ea; mso-bidi-font-family: +mn-cs; mso-fareast-theme-font: minor-fareast; mso-bidi-theme-font: minor-bidi; color: black; mso-color-index: 1; mso-font-kerning: 12.0pt; language: en-GB; mso-style-textfill-type: solid; mso-style-textfill-fill-themecolor: text1; mso-style-textfill-fill-color: black; mso-style-textfill-fill-alpha: 100.0%;">At Xeluxe, we believe that effective Fire &amp; Life Safety design is the foundation of resilient and sustainable built environments. Our approach goes beyond compliance &mdash; we integrate advanced fire protection, detection, and life safety systems seamlessly into the design process to enhance safety, functionality, and operational continuity. With a strong foundation in prescriptive code application backed by extensive industrial collaboration with Fire protection system manufacturers and vendors, our team delivers innovative solutions tailored to each project&rsquo;s unique risks and architectural vision. With our strong industry partnerships and an in-depth understanding of the latest technologies and international standards, we ensure every design is technically sound, cost-efficient, and future-ready. From concept to commissioning, we work collaboratively with clients, contractors, and authorities to deliver comprehensive, reliable, and compliant fire and life safety strategies that protect people, property, and purpose.</span></p>\r\n</div>\r\n</div>\r\n</div>\r\n<div class="container py-4">\r\n<div class="row">\r\n<div class="col-md-6">\r\n<h3><img src="../uploads/images/17.jpg" alt="" width="438" height="246"></h3>\r\n<p style="language: en-AE; line-height: 115%; margin-top: 12.0pt; margin-bottom: 8.0pt; margin-left: 0in; text-align: justify; text-justify: inter-ideograph; direction: ltr; unicode-bidi: embed; mso-line-break-override: none; word-break: normal; punctuation-wrap: hanging;"><span style="font-size: 11.0pt; font-family: \'Century Gothic\'; mso-ascii-font-family: \'Century Gothic\'; mso-fareast-font-family: Aptos; mso-bidi-font-family: Arial; color: black; mso-color-index: 1; mso-font-kerning: 1.0pt; language: en-GB; font-weight: bold; mso-style-textfill-type: solid; mso-style-textfill-fill-themecolor: text1; mso-style-textfill-fill-color: black; mso-style-textfill-fill-alpha: 100.0%;">Fire protection &amp; Suppression system Design &amp; Peer Review</span></p>\r\n<p style="language: en-AE; line-height: 115%; margin-top: 12.0pt; margin-bottom: 8.0pt; margin-left: 0in; text-align: justify; text-justify: inter-ideograph; direction: ltr; unicode-bidi: embed; mso-line-break-override: none; word-break: normal; punctuation-wrap: hanging;"><span style="font-size: 11.0pt; font-family: \'Century Gothic\'; mso-ascii-font-family: \'Century Gothic\'; mso-fareast-font-family: Aptos; mso-bidi-font-family: Arial; color: black; mso-color-index: 1; mso-font-kerning: 1.0pt; language: en-GB; mso-style-textfill-type: solid; mso-style-textfill-fill-themecolor: text1; mso-style-textfill-fill-color: black; mso-style-textfill-fill-alpha: 100.0%;">At Xeluxe, our expertise in fire protection and suppression system design covers every stage of a project. Every solution is developed in strict accordance with local building and fire code requirements, maintaining a strong focus on cost-effectiveness, constructability, and ease of installation. We remain actively invo