INSERT INTO `cms_vision_mission` (`id`, `section_title`, `section_subtitle`, `vision_content`, `mission_content`, `updated_at`) VALUES
	(1, 'Vision, Mission & Values', 'Our foundation principles that guide every decision, project, and partnership as we work to create a safer, more sustainable built environment.', '<p>To lead the way in creating a safer, more sustainable, and resilient built environment &mdash; where people, projects, and systems perform at their best.</p>\r\n', '<p>To be the trusted and preferred specialist engineering company for our partners in the built environment, fostering lasting value by protecting life, preserving assets, and delivering resilient, high-performance solutions.</p>\r\n', '2025-11-17 21:20:35');
